import React, { useState } from 'react'
import './todolist.css'
import { Button, Divider, Typography } from 'antd';
import { Input } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { PlusOutlined } from '@ant-design/icons';
import Todo from '../Components/Todo/Todo';
import { Progress } from 'antd';

export default function ToDoListComponent() {
  const {Title} = Typography;
  const [state,setState] = useState({});
  const toDoList = useSelector(state => state.ToDoReducer);
  console.log("todo",toDoList)

  const dispatch = useDispatch();

  const toDo = (e)=>{
    let {value} = e.target;
    setState({...state,content: value});
  }

  const dispatchToDo = ()=>{
    dispatch({
      todo: state,
      type: "todoDispatch"
    });
    setState({content:""});
  }

  const renderList = ()=>{
    console.log("todo",toDoList)

    return toDoList.todo.map((todo,index)=>{
      return <Todo content={todo.content} index={index} />
    })
  }

  return (
    <div className="background">
      <div className="content">
        <Title style={{color: "#485056"}}>TODOLIST</Title>
        <div className="content__input">
          <Input style={{fontStyle: "italic",textAlign: "center"}} placeholder="What need to be done" onChange={toDo} value={state?.content} />
          <PlusOutlined className="abc" onClick={dispatchToDo} />
        </div>
        <Divider/>
        {renderList()}
        <div className="content__footer">
          <Progress />
          <Button>Remove checked box</Button>
        </div>
      </div>
    </div>
  )
}
