import React, { useState } from 'react'
import { CloseOutlined, EditFilled } from '@ant-design/icons';
import { Checkbox } from 'antd';
import { Divider } from 'antd';
import { useDispatch } from 'react-redux';

export default function Todo(props) {

    const {content,index} = props;

    const [state,setState]=useState({content});
    const [disabled,setDisabled]=useState(true);

    const dispatch = useDispatch();

    const onChange = (e) => {
        console.log(`checked = ${e.target.checked}`);
      };

    const handleChange = (e)=>{
      const {value} = e.target;
      setState({
        content: value
      })
    }

    const editToDo = (content)=>{
      if(disabled === true){
        dispatch({
          todo:content,
          type: "editToDo"
        })
      }
    }

    const deleteToDo = (content)=>{
      dispatch({
        todo:content,
        type: "deleteToDo"
      })
    }

  return (
    <>
      <div className="todolist">
            <Checkbox onChange={onChange} />
            <input type="text" value={state.content} disabled={disabled} onChange={handleChange} />
            <EditFilled onClick={ async () => {  
              await setDisabled(!disabled);
              // if(disabled === true){
              //   editToDo({content:state.content,key:index})
              // }
              editToDo()
            }} />
            <CloseOutlined onClick={()=>{
              deleteToDo(index)
            }} />
      </div>
      <Divider/>
    </>
  )
}
