import React, { useEffect, useState } from 'react'
import {Input} from 'antd'

export default function InputConponent() {

    const [state,setState] = useState({Ten:"",Tuoi:"",DiaChi:"",ChieuCao:"",CanNang:""});

    const handleChange = (e)=>{
        let {name,value} = e.target;
        setState({...state,[name]: value});
    };

    const handleSubmit = (e)=>{
      e.preventDefault();
      localStorage.setItem("information",JSON.stringify(state));
    }

    const checkInfor = ()=>{
      if(localStorage.getItem("information")){
        const infor = JSON.parse(localStorage.getItem("information"));
        setState({
          ...infor
        })
        console.log(state)
      }
    }

    useEffect(()=>{
      checkInfor();
    },[])

  return (
    <div style={{width: "25%", marginLeft: "35%"}}>
        <form onSubmit={handleSubmit}>
            <Input onChange={handleChange} name="Ten" placeholder="Tên" value={state.Ten != "" ? state.Ten : ""} ></Input>
            <Input onChange={handleChange} name="Tuoi" placeholder="Tuổi"  ></Input>
            <Input onChange={handleChange} name="DiaChi" placeholder="Địa chỉ"  ></Input>
            <Input onChange={handleChange} name="ChieuCao" placeholder="Chiều cao"  ></Input>
            <Input onChange={handleChange} name="CanNang" placeholder="Cân nặng" ></Input>
            <button>Submit</button>
        </form>
    </div>
  )
}
